const formatBytes = (bytes) => {
  const units = ['b', 'Kb', 'Mb', 'Gb', 'Tb'];
  let index = 0;
  while (bytes >= 1000 && index < units.length - 1) {
    bytes /= 1000;
    index++;
  }
  return bytes.toFixed(2) + ' ' + units[index];
};

